package com.vst.cfs;

public class PyramidExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int index_i=1;index_i<=5;index_i++){  
			for(int index_j=1;index_j<=index_i;index_j++){  
			        System.out.print("* ");  
			}  
			System.out.println();//new line  
	}
	}
}
