package com.vst.cfs;

public class While_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	    int countnum = 2;
	    while (countnum < 20) {
	      System.out.println("Count is: " + countnum);
	      countnum = countnum + 1;
	    }

	    for (int x = 2; (x < 20); x = x + 1) {
	      System.out.println("x=" + x);
	    }

	}

}
